function checkEmail(b){if(b.email.value===""){alert(getMsg("Please enter your email address!"));b.email.focus();return false}var a=/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;if(b.email.value===""||!a.test(b.email.value)){alert(getMsg("Invalid email address!"));b.email.focus();return false}return true};