function checkPass(a){if(a.password.value===""){alert(getMsg("Password is too long or too short"));a.password.focus();return false}if(a.password.value.length<6||a.password.value.length>20){alert(getMsg("Password is too long or too short"));a.password.focus();return false}if(a.conpass.value===""){alert(getMsg("Please confirm your password!"));a.conpass.focus();return false}if(a.conpass.value!==a.password.value){alert(getMsg("Inconsistent password!"));a.conpass.focus();return false}return true};